import React, { useState } from 'react';
import './Counter.css';

function Counter() {
  const [count, setCount] = useState(0);

  const handleIncrement = () => {
    setCount(count + 1);
  };

  const handleDecrement = () => {
    if (count > 0) {
      setCount(count - 1);
    }
  };

  const handleReset = () => {
    setCount(0);
  };

  return (
    <div className="counter-container">
      <div className="counter-card">
        <h1>Counter Application</h1>
        <p className="subtitle">Learning React useState Hook</p>

        <div className="counter-display">
          <p className="label">Current Count</p>
          <p className="count-value">{count}</p>
        </div>

        {count === 0 && (
          <div className="warning-message">
            ‚ö†Ô∏è Minimum limit reached
          </div>
        )}

        <div className="button-group">
          <button 
            className="btn btn-increment" 
            onClick={handleIncrement}
          >
            ‚ûï Increment (+1)
          </button>

          <button 
            className="btn btn-decrement" 
            onClick={handleDecrement}
            disabled={count === 0}
          >
            ‚ûñ Decrement (-1)
          </button>

          <button 
            className="btn btn-reset" 
            onClick={handleReset}
          >
            üîÑ Reset (0)
          </button>
        </div>

        <div className="info-box">
          <strong>How it works:</strong> Click the buttons to see the counter 
          update in real-time using React's useState hook!
        </div>
      </div>
    </div>
  );
}

export default Counter;
